import { Router, ActivatedRoute } from '@angular/router';
import { Component } from '@angular/core';

interface ItemData {
  name: string;
  age: number | string;
  address: string;
  checked: boolean;
  expand: boolean;
  description: string;
  disabled?: boolean;
}

@Component({
  templateUrl: './product-category.component.html',
  styleUrls: ['./product-category.component.scss']
})
export class ProductCategoryComponent {

  loading = false;

  listOfData: ReadonlyArray<ItemData> = [];
  displayData: ReadonlyArray<ItemData> = [];
  allChecked = false;
  indeterminate = false;
  fixedColumn = true;
  scrollX: string | null = null;
  scrollY: string | null = null;

  currentPageDataChange($event: ReadonlyArray<ItemData>): void {
    this.displayData = $event;
    this.refreshStatus();
  }

  refreshStatus(): void {
    const validData = this.displayData.filter(value => !value.disabled);
    const allChecked = validData.length > 0 && validData.every(value => value.checked === true);
    const allUnChecked = validData.every(value => !value.checked);
    this.allChecked = allChecked;
    this.indeterminate = !allChecked && !allUnChecked;
  }

  generateData(): ReadonlyArray<ItemData> {
    const data = [];
    for (let i = 1; i <= 100; i++) {
      data.push({
        name: 'John Brown',
        age: `${i}2`,
        address: `New York No. ${i} Lake Park`,
        description: `My name is John Brown, I am ${i}2 years old, living in New York No. ${i} Lake Park.`,
        checked: false,
        expand: false
      });
    }
    return data;
  }

  constructor(
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.listOfData = this.generateData();
  }

  getProdcutDetail(): void {
    this.router.navigate(['../', 'detail', 2], { relativeTo: this.route });
  }

  fallback =
  'data:image/png;base64,';

}
